const expect = require('chai').expect;
const factorial = require('../index').factorial;

/* global define, it, describe, before, beforeEach, afterEach, after */
describe('Testing factorial function', () => {
  it('Sould return 1 to 1!', () => {
    expect(factorial(1)).to.be.equal('1');
  });
  it('Sould return 120 to 5!', () => {
    expect(factorial(5)).to.be.equal('120');
  });
  it('Sould return 362880 to 9!', () => {
    expect(factorial(9)).to.be.equal('362880');
  });
  it('Sould return 1307674368000 to 15!', () => {
    expect(factorial(15)).to.be.equal('1307674368000');
  });
  it('Sould return 3969937160808720895401959629498630647790406360168322301129748464310422041758630649341780708631240196854767624444057168110272995649603642560353748940315749184568295424000000000000000000000000000 to 117!', () => {
    expect(factorial(117)).to.be.equal('3969937160808720895401959629498630647790406360168322301129748464310422041758630649341780708631240196854767624444057168110272995649603642560353748940315749184568295424000000000000000000000000000');
  });
  it('Sould return  to 178!', () => {
    expect(factorial(178)).to.be.equal('');
  });
});